#![deny(missing_docs)]

use k8s_openapi::serde;
use kube::CustomResource;
use schemars::JsonSchema;
use serde::{Deserialize, Serialize};

/// Struct corresponding to the Specification (`spec`) part of the `Echo` resource, directly
/// reflects context of the `echoes.example.com.yaml` file to be found in this repository.
/// The `Echo` struct will be generated by the `CustomResource` derive macro.
#[derive(CustomResource, Serialize, Deserialize, Debug, PartialEq, Clone, JsonSchema)]
#[kube(
group = "example.com",
version = "v1",
kind = "Echo",
plural = "echoes",
derive = "PartialEq",
namespaced
)]
pub struct EchoSpec {
  pub replicas: i32,
}

#[derive(CustomResource, Deserialize, Serialize, Clone, Debug, JsonSchema)]
#[kube(
group = "yurikrupnik.dev",
version = "v1",
kind = "Topology",
namespaced
)]
#[kube(status = "TopologyStatus")]
#[kube(scale = r#"{"specReplicasPath":".spec.replicas", "statusReplicasPath":".status.replicas"}"#)]
pub struct TopologySpec {
  pub name: String,
  pub info: String,
  pub nodes: Vec<String>,
  pub image: Option<String>,
}

#[derive(Deserialize, Serialize, Clone, Debug, Default, JsonSchema)]
pub struct TopologyStatus {
  pub is_bad: bool,
  pub is_good: bool,
}

